mapper.select:
		SELECT SUM(point)
   		FROM Mileage
    	WHERE user_id = #{userId}

SELECT mileage_id mileageId, review_id reviewId, user_id userId, type, comment, point, inserted FROM Mileage WHERE user_id = #{userId} GROUP BY mileage_id
ORDER BY inserted DESC
ORDER BY inserted DESC LIMIT 0, #{page}
